    public static $aliases = [];

    public static function stroke_or_fill($code)
    {
        if (strpos('stroke-', $code) !== false) {
            return 'stroke';
        } else {
            if (in_array($code, self::$stroke_icons_list)) {
                return 'stroke';
            } else {
                return 'fill';
            }
        }
    }

    public static function get_icon($icon = '', $fill = '')
    {

        /**
         * Define the list of icons
         */
        $list = self::$icons;

        /**
         * Check if alias exists only if networks does not have an icon
         */
        if (!array_key_exists($icon, $list) && array_key_exists($icon, self::$aliases)) {
            $icon = self::$aliases[$icon];
        }

        if (array_key_exists($icon, $list)) {
            $stroke_or_fill = self::stroke_or_fill($icon);

            $repl = '<svg class="essb-ui-svg-icon essb-ui-svg-icon-' . esc_attr($icon) . ' essb-ui-svg-mode-' . esc_attr($stroke_or_fill) . '" aria-hidden="true" role="img" focusable="false" ';

            if (!empty($fill)) {
                $repl .= 'fill="' . esc_attr($fill) . '" ';
            }

            $svg  = preg_replace('/^<svg /', $repl, trim($list[$icon])); // Add extra attributes to SVG code.
            $svg  = preg_replace("/([\n\t]+)/", ' ', $svg); // Remove newlines & tabs.
            $svg  = preg_replace('/>\s*</', '><', $svg);    // Remove whitespace between SVG tags.

            // Make sure that only our allowed tags and attributes are included.
            $svg = wp_kses(
                $svg,
                array(
                    'svg'     => array(
                        'class'       => true,
                        'xmlns'       => true,
                        'width'       => true,
                        'height'      => true,
                        'viewbox'     => true,
                        'aria-hidden' => true,
                        'role'        => true,
                        'focusable'   => true,
                    ),
                    'path'    => array(
                        'fill'      => true,
                        'fill-rule' => true,
                        'd'         => true,
                        'transform' => true,
                        'stroke-width'  => true,
                        'stroke-linecap' => true,
                        'stroke-linejoin' => true,
                    ),
                    'polyline'    => array(
                        'fill'      => true,
                        'fill-rule' => true,
                        'points'         => true,
                        'transform' => true,
                        'stroke-width'  => true,
                        'stroke-linecap' => true,
                        'stroke-linejoin' => true,
                    ),
                    'line'    => array(
                        'fill'      => true,
                        'fill-rule' => true,
                        'x1'         => true,
                        'x2' => true,
                        'y1'         => true,
                        'y2' => true,
                        'stroke-width'  => true,
                        'stroke-linecap' => true,
                        'stroke-linejoin' => true,
                    ),
                    'polygon' => array(
                        'fill'      => true,
                        'fill-rule' => true,
                        'points'    => true,
                        'transform' => true,
                        'focusable' => true,
                        'stroke-width'  => true,
                        'stroke-linecap' => true,
                        'stroke-linejoin' => true,
                    ),
                    'circle' => array(
                        'fill'      => true,
                        'fill-rule' => true,
                        'points'    => true,
                        'cx'    => true,
                        'cy'    => true,
                        'r'    => true,
                        'transform' => true,
                        'focusable' => true,
                        'stroke-width'  => true,
                        'stroke-linecap' => true,
                        'stroke-linejoin' => true,
                    ),
                    'rect' => array(
                        'fill'      => true,
                        'fill-rule' => true,
                        'points'    => true,
                        'transform' => true,
                        'focusable' => true,
                        'x' => true,
                        'y' => true,
                        'width' => true,
                        'height' => true,
                        'rx' => true,
                        'stroke-width'  => true,
                        'stroke-linecap' => true,
                        'stroke-linejoin' => true,
                    ),
                )
            );

            if (! $svg) {
                return null;
            }

            return $svg;
        }

        return null;
    }

    /**
     * Generate UI icon code
     * @param mixed $icon
     * @param mixed $fill
     * @return string
     */
    public static function generate_icon($icon = '', $fill = '', $size = '')
    {
        $icon = self::get_icon($icon, $fill);

        if (!empty($icon)) {
            return '<span class="essb-ui-icon essb-ui-icon-' . self::stroke_or_fill($icon) . (!empty($size) ? ' essb-ui-icon-size-' . $size : '') . '">' . $icon . '</span>';
        } else {
            return '';
        }
    }
